import React, { useState, useEffect } from 'react';
import DocumentRenderer from './components/DocumentRenderer';
import jsonData from './input/input.json';


const ContractRenderer = ({ data }) => {
    if (!data || !Array.isArray(data)) return null;

    const renderNode = (node) => {
        if (typeof node.text === "string") {
            let style = {};
            if (node.bold) style.fontWeight = "bold";
            if (node.underline) style.textDecoration = "underline";
            if (node.color) style.backgroundColor = node.color;

            return <span style={style}>{node.text}</span>;
        }

        switch (node.type) {
            case "h1":
                return <h1>{node.children.map(renderNode)}</h1>;
            case "h4":
                return <h4>{node.children.map(renderNode)}</h4>;
            case "p":
                return <p>{node.children.map(renderNode)}</p>;
            case "ul":
                return <ul>{node.children.map(renderNode)}</ul>;
            case "li":
                return <li>{node.children.map(renderNode)}</li>;
            case "lic":
                return <>{node.children.map(renderNode)}</>;
            case "mention":
                return <span style={{ backgroundColor: node.color }}>{node.children.map(renderNode)}</span>;
            case "block":
            case "clause":
                return <div>{node.children.map(renderNode)}</div>;
            default:
                return null;
        }
    };

    return <div>{data.map(renderNode)}</div>;
};

function App() {
    const [data, setData] = useState(null);

    useEffect(() => {
        console.log('JSON Data:', jsonData);
        setData(jsonData);
    }, []);

    if (!data) {
        console.log('Data is not loaded yet.');
        return <div>Loading...</div>;
    }

    console.log('Rendering with data:', data);
    return (
        <div className="p-8">
            <DocumentRenderer data={data} />
        </div>
    );
}

export default App;
