import React from 'react';

const ContractRenderer = ({ data }) => {
    if (!Array.isArray(data)) return null; // early exit if data is not an array

    const renderNode = (node, clauseCount = { clause: 1 }) => {
        if (!node) return null;

        // Handle text node and apply styles
        if (typeof node.text === 'string') {
            const style = {
                fontWeight: node.bold ? 'bold' : 'normal',
                textDecoration: node.underline ? 'underline' : 'none',
                backgroundColor: node.color || 'transparent',
            };

            return node.text.split('\n').map((part, index) => (
                <React.Fragment key={index}>
                    <span style={style}>{part}</span>
                    {index < node.text.split('\n').length - 1 && <br />}
                </React.Fragment>
            ));
        }

        // Handle other node types
        if (!node.type || !node.children) return null;

        const children = node.children.map((child, index) => renderNode(child, clauseCount));

        switch (node.type) {
            case 'h1':
                return <h1 key={node.key}>{children}</h1>;
            case 'h4':
                return (
                    <h4 key={node.key}>
                        <span>{`${clauseCount.clause++}. `}</span>
                        {children}
                    </h4>
                );
            case 'p':
                return <p key={node.key}>{children}</p>;
            case 'ul':
                return <ul key={node.key}>{children}</ul>;
            case 'li':
                return <li key={node.key}>{children || node.text}</li>;
            case 'mention':
                return <span key={node.key} style={{ backgroundColor: node.color }}>{children}</span>;
            case 'block':
            case 'clause':
                return <div key={node.key}>{children}</div>;
            default:
                console.warn('Unknown node type:', node.type);
                return null;
        }
    };

    return <div>{data.map((node, index) => renderNode(node, { clause: 1 }))}</div>;
};

export default ContractRenderer;
