import React from 'react';

function TextRenderer({ node }) {
  if (!node) {
    console.log('TextRenderer: Node is undefined.'); // Check if node is undefined
    return null;
  }

  console.log('TextRenderer node:', node); // Check the node being processed

  let style = {};
  if (node.marks) {
    if (node.marks.some((mark) => mark.type === 'bold')) {
      style.fontWeight = 'bold';
    }
    if (node.marks.some((mark) => mark.type === 'italic')) {
      style.fontStyle = 'italic';
    }
    if (node.marks.some((mark) => mark.type === 'underline')) {
      style.textDecoration = 'underline';
    }
  }

  return <span style={style}>{node.text}</span>;
}

export default TextRenderer;
