import React, { useState, useEffect } from 'react';
import jsonData from './input/input.json';

const ContractRenderer = ({ data }) => {
    if (!data || !Array.isArray(data)) return null;

    const renderNode = (node, key, headerCount = { h4: 1 }) => {
        if (!node) return null; // Handle missing node

        // Render text nodes
        if (typeof node.text === "string") {
            let style = {};
            if (node.bold) style.fontWeight = "bold";
            if (node.underline) style.textDecoration = "underline";
            if (node.color) style.backgroundColor = node.color;

            const textParts = node.text.split('\n');
            return textParts.map((part, index) => (
                <React.Fragment key={`${parentKey}-${index}`}>
                    <span style={style}>{part}</span>
                    {index < textParts.length - 1 && <br />} {/* Add <br /> between parts */}
                </React.Fragment>
            ));
        }

        // Handle invalid node structure
        if (!node.type || !node.children) return null;

        // Render nodes based on type
        switch (node.type) {
            case 'h1':
                return <h1 key={key}>{renderChildren()}</h1>;
            case 'clause':
                // Add numbering for clauses
                const clauseNumber = `${headerCount.clause}. `;
                headerCount.clause += 1; // Increment clause number for the next clause

                return (
                    <div key={key}>
                        <span>{clauseNumber}</span>
                        {renderChildren()}
                    </div>
                );
            case 'p':
                return <p key={key}>{renderChildren()}</p>; // Simplified case for 'p'
            case 'ul':
                return <ul key={key}>{renderChildren()}</ul>;
            case 'li':
                return <li key={key}>{renderChildren()}</li>;
            case 'lic':
                return <React.Fragment key={key}>{renderChildren()}</React.Fragment>;
            case 'mention':
                return <span key={key} style={{ backgroundColor: node.color }}>{renderChildren()}</span>;
            case 'block':
                return <div key={key}>{renderChildren()}</div>;
            default:
                console.warn('Unknown node type:', node.type); // Log unknown node types
                return null;
        }
    };

    return <div>{data.map((node, index) => renderNode(node, index))}</div>;
};

function App() {
    const [data, setData] = useState(null);

    useEffect(() => {
        console.log('JSON Data:', JSON.stringify(jsonData, null, 2)); // Log formatted JSON
        setData(jsonData);
    }, []);

    if (!data) {
        console.log('Data is not loaded yet.');
        return <div>Loading...</div>;
    }

    console.log('Rendering with data:', data);
    return (
        <div className="p-8">
            <ContractRenderer data={data} />
        </div>
    );
}

export default App;
