import React from 'react';

const ContractRenderer = ({ data }) => {
    if (!data || !Array.isArray(data)) return null; //if missing or parsed wrong

    const renderNode = (node, key, clauseCount = { clause: 1 }, parentKey = '') => {
        if (!node) return null;

        // for the text node
        if (typeof node.text === 'string') {
            // check styles
            let style = {};
            if (node.bold) style.fontWeight = 'bold';
            if (node.underline) style.textDecoration = 'underline';
            if (node.color) style.backgroundColor = node.color;

            const textParts = node.text.split('\n');
            return textParts.map((part, index) => (
                <React.Fragment key={`${parentKey}-${index}`}>
                    <span style={style}>{part}</span>
                    {index < textParts.length - 1 && <br />} {/*handling /n*/}
                </React.Fragment>
            ));
        }

        if (!node.type || !node.children) return null;

        switch (node.type) {
            case 'h1':
                return (
                    <h1 key={key}>
                        {node.children.map((child, index) =>
                            renderNode(child, `${key}-${index}`, clauseCount, `${key}-${index}`)
                        )}
                    </h1>
                );

            case 'h4':
                const clauseNumber = `${clauseCount.clause}. `;
                clauseCount.clause += 1; // Increment clause count

                return (
                    <h4 key={key}>
                        <span>{clauseNumber}</span>
                        {node.children.map((child, index) =>
                            renderNode(child, `${key}-${index}`, clauseCount, `${key}-${index}`)
                        )}
                    </h4>
                );

            case 'p':
                return (
                    <p key={key}>
                        {node.children.map((child, index) => {
                            return renderNode(child, `${key}-${index}`, clauseCount, `${key}-${index}`);
                        })}
                    </p>
                );

            case 'ul':
                return (
                    <ul key={key}>
                        {node.children.map((child, index) =>
                            renderNode(child, `${key}-${index}`, clauseCount, `${key}-${index}`)
                        )}
                    </ul>
                );

            case 'li':
                return (
                    <li key={key}>
                        {node.children && node.children.length > 0 ? (
                            node.children.map((child, index) =>
                                renderNode(child, `${key}-${index}`, clauseCount, `${key}-${index}`)
                            )
                        ) : (
                            node.text && <span>{node.text}</span>
                        )}
                    </li>
                );

            case 'lic':
                return (
                    <React.Fragment key={key}>
                        {node.children.map((child, index) =>
                            renderNode(child, `${key}-${index}`, clauseCount, `${key}-${index}`)
                        )}
                    </React.Fragment>
                );

            case 'mention':
                return (
                    <span key={key} style={{ backgroundColor: node.color }}>
                        {node.children.map((child, index) =>
                            renderNode(child, `${key}-${index}`, clauseCount, `${key}-${index}`)
                        )}
                    </span>
                );

            case 'block':
            case 'clause':
                return (
                    <div key={key}>
                        {node.children.map((child, index) =>
                            renderNode(child, `${key}-${index}`, clauseCount, `${key}-${index}`)
                        )}
                    </div>
                );

            default:
                console.warn('Unknown node type:', node.type);
                return null;
        }
    };

    return <div>{data.map((node, index) => renderNode(node, `${index}`, { clause: 1 }))}</div>;
};

export default ContractRenderer;
