import React, { useState, useEffect } from 'react';
import PropTypes from 'prop-types'; // For prop validation
import jsonData from './input/input.json';

// Helper function to get text styles
const getTextStyles = (node) => {
    let style = {};
    if (node.bold) style.fontWeight = 'bold';
    if (node.underline) style.textDecoration = 'underline';
    if (node.color) style.backgroundColor = node.color;
    return style;
};

// ContractRenderer Component
const ContractRenderer = React.memo(({ data }) => {
    if (!data || !Array.isArray(data)) return null;

    const renderNode = (node, key) => {
        if (!node) return null; // Handle missing node

        // Render text nodes
        if (typeof node.text === 'string') {
            return <span key={key} style={getTextStyles(node)}>{node.text}</span>;
        }

        // Handle invalid node structure
        if (!node.type || !node.children) return null;

        // Render nodes based on type
        switch (node.type) {
            case 'h1':
                return <h1 key={key}>{node.children.map((child, index) => renderNode(child, index))}</h1>;
            case 'h4':
                return <h4 key={key}>{node.children.map((child, index) => renderNode(child, index))}</h4>;
            case 'p':
                return <p key={key}>{node.children.map((child, index) => renderNode(child, index))}</p>;
            case 'ul':
                return <ul key={key}>{node.children.map((child, index) => renderNode(child, index))}</ul>;
            case 'li':
                return <li key={key}>{node.children.map((child, index) => renderNode(child, index))}</li>;
            case 'lic':
                return <React.Fragment key={key}>{node.children.map((child, index) => renderNode(child, index))}</React.Fragment>;
            case 'mention':
                return <span key={key} style={{ backgroundColor: node.color }}>{node.children.map((child, index) => renderNode(child, index))}</span>;
            case 'block':
            case 'clause':
                return <div key={key}>{node.children.map((child, index) => renderNode(child, index))}</div>;
            default:
                return null;
        }
    };

    return <div>{data.map((node, index) => renderNode(node, index))}</div>;
});

// Prop validation for ContractRenderer
ContractRenderer.propTypes = {
    data: PropTypes.arrayOf(
        PropTypes.shape({
            type: PropTypes.string,
            text: PropTypes.string,
            children: PropTypes.array,
            bold: PropTypes.bool,
            underline: PropTypes.bool,
            color: PropTypes.string,
        })
    ).isRequired,
};

// App Component
function App() {
    const [data, setData] = useState(null);

    useEffect(() => {
        console.log('JSON Data:', JSON.stringify(jsonData, null, 2)); // Log formatted JSON
        setData(jsonData);
    }, []);

    if (!data) {
        console.log('Data is not loaded yet.');
        return <div>Loading...</div>;
    }

    console.log('Rendering with data:', data);
    return (
        <div className="p-8">
            <ContractRenderer data={data} />
        </div>
    );
}

export default App;
