import React, { useContext } from 'react';
import NodeRenderer from "./NodeRenderer";

const ClauseContext = React.createContext(0);

function ClauseRenderer({ node }) {
    const clauseNumber = useContext(ClauseContext);

    return (
        <ClauseContext.Provider value={clauseNumber + 1}>
            <div className="my-4">
                <span className="font-bold">{clauseNumber}.</span>
                {node.children.map((child, index) => (
                    <NodeRenderer key={index} node={child} />
                ))}
            </div>
        </ClauseContext.Provider>
    );
}

export default ClauseRenderer;
